import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html'
})
export class CreateComponent {
  public transaction: ITransaction = new Transaction();

  constructor(private http: HttpClient) {

  }

  submit() {
    debugger;
    this.http.post<ITransaction>('/api/transaction', this.transaction).subscribe(result => {

    }, error => console.error(error));
  }
}

interface ITransaction {
  name: string;
  date: string;
  cost: number;
}

class Transaction implements Transaction {
  constructor(private name = '', private date: Date = new Date(), private cost: number = 1) {
  }
}
<div>
  <div class="form-group mb-2">
    Name: <input type="text" [(ngModel)]="transaction.name" />
  </div>
  <div class="form-group mb-2">
    Cost: <input type="number" />
  </div>
  <div class="form-group mb-2">
    Date: <input type="date" />
  </div>
  <button (click)="submit()">Submit</button>
</div>
