import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html'
})
export class CreateComponent {
  public transaction: Transaction = new Transaction();

  constructor(private http: HttpClient, private _router: Router) {

  }
  submitted = false;

  onSubmit() { this.submitted = true; }

  submit() {

    debugger;
    this.http.post<Transaction>('/api/transaction', this.transaction).subscribe(result => {
      this._router.navigateByUrl(`/view/${result}`);
    }, error => console.error(error));
  }
}

export class Transaction {
  constructor(public name = '', public date?: Date, public cost: number = 1) {
  }
}

<form #myForm="ngForm" (ngSubmit)="submit()">
  <div class="row align-items-center">
    <div class="col">
      <h4>
        Create Transaction
      </h4>
    </div>
    <div class="col-auto">
      <div>
        <button type="submit" [disabled]="!myForm.form.valid" class="btn btn-primary">Submit</button>

        <a routerLink="/" class="ms-2 btn btn-outline-secondary">
          Back
        </a>
      </div>
    </div>
  </div>

  <div class="mt-2">

    <div class="form-floating mb-3">
      <input type="text" [(ngModel)]="transaction.name" id="name" name="name" class="form-control" placeholder="Transaction Name" required />
      <label for="name">Transaction Name</label>
    </div>

    <div class="row row-cols-sm-2">
      <div>
        <div class="form-floating mb-3">
          <input type="number" [(ngModel)]="transaction.cost" id="cost" name="cost" class="form-control" placeholder="Cost" required />
          <label for="cost">Cost</label>
        </div>
      </div>
      <div>
        <div class="form-floating mb-3">
          <input type="date" [(ngModel)]="transaction.date" id="date" name="date" class="form-control" placeholder="Date" required />
          <label for="date">Date</label>
        </div>
      </div>
    </div>
  </div>
</form>
