import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html'
})
export class CreateComponent {
  public transaction: Transaction = new Transaction();

  constructor(private http: HttpClient) {

  }
  submitted = false;

  onSubmit() { this.submitted = true; }

  submit() {

    debugger;
    this.http.post<Transaction>('/api/transaction', this.transaction).subscribe(result => {

    }, error => console.error(error));
  }
}

export class Transaction {
  constructor(public name = '', public date: Date = new Date(), public cost: number = 1) {
  }
}
<form #myForm="ngForm" (ngSubmit)="submit()">
  <div class="form-group mb-2">
    Name: <input type="text" [(ngModel)]="transaction.name" id="name" name="name" required/>
    {{transaction.name}}
  </div>
  <div class="form-group mb-2">
    Cost: <input type="number" [(ngModel)]="transaction.cost" id="cost" name="cost" required/>
  </div>
  <div class="form-group mb-2">
    Date: <input type="date" [(ngModel)]="transaction.date" [ngModel]="transaction.date | date:'dd/MM/yyyy'" id="date" name="date" required/>
  </div>
  <button type="submit" [disabled]="!myForm.form.valid">Submit</button>
</form>
